import React from 'react';
import Card from 'react-bootstrap/Card'
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button'
import axios from 'axios';
export class ShowCurrentItems extends React.Component {
    constructor() {
        super();
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeStock = this.onChangeStock.bind(this);
        this.onChangeImage = this.onChangeImage.bind(this);
        this.state = {
            Name: '',
            Price: '',
            Stock: '',
            Image: '',
        }

    }
    Buyone(e){
        const newItem={
            Name:this.state.Name,
            Price:this.state.Price,
            Stock:this.state.Stock-1,
            Image:this.state.Image,
            _id:this.state._id
        }
        axios.put('http://localhost:4000/api/items/'+this.state._id,newItem).then((res)=>{
            console.log(res);
        })
    }

    render() {
        return (
            <div class="container">
               
                <Card> 
                    <Card.Body>
                        <blockquote className="blockquote mb-0">
                            <img src={this.props.item.Image} width="200" height="200"></img> 
                            <Card.Header> <h4>{this.props.item.Name}</h4></Card.Header>
                            <p>{this.props.item.Stock}</p>
                            <p>$ {this.props.item.Price}</p>
                        </blockquote>
                    </Card.Body>
                    <Button variant="danger" onClick={this.Buyone}>Buy 1</Button>
                </Card>
                

            </div>

        );
    }
}
