import React from 'react';
import axios from 'axios';
export class AddItem extends React.Component {//Returns below text when called
    constructor() {
        super();
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeStock = this.onChangeStock.bind(this);
        this.onChangeImage = this.onChangeImage.bind(this);
        this.state = {
            Name: '',
            Price: '',
            Stock: '',
        }

    }
    onChangeName(e) {
        this.setState({
            Name: e.target.value
        })
    }
    onChangePrice(e) {
        this.setState({
            Year: e.target.value
        })
    }
    onChangeStock(e) {
        this.setState({
            Poster: e.target.value
        })
    }
    onChangeImage(e) {
        this.setState({
            Image: e.target.value
        })
    }
    onSubmit(e) {
        e.preventDefault();
        const newItem={
            Name:this.state.Name,
            Price:this.state.Price,
            Stock:this.state.Stock,
            Image:this.state.Image
        }
        axios.post('http://localhost:4000/api/item',newItem).then((res)=>{
            console.log(res);
        })
        .catch((err)=>{
            console.log(err);
        });
    }
    render() {
        return (
            <div className='App'>
                <h1>Add new item</h1>
                <form onSubmit={this.onSubmit}>
                    <div className='form-group'>
                        <label>Item Name</label>
                        <input type='text' className='form-control' value={this.state.Name} onChange={this.onChangeName}></input>
                    </div>
                    <div className='form-group'>
                        <label>Price</label>
                        <input type='text' className='form-control' value={this.state.Price} onChange={this.onChangePrice}></input>
                    </div>
                    <div className='form-group'>
                        <label>Stock</label>
                        <input type='text' className='form-control' value={this.state.Stock} onChange={this.onChangePoster}></input>
                    </div>
                    <div className='form-group'>
                        <label>Image</label>
                        <input type='text' className='form-control' value={this.state.Image} onChange={this.onChangePoster}></input>
                    </div>
                    <div className='form-group'>
                        <input type='submit' value='Add Movie' className='btn btn-primary'></input>
                    </div>
                </form>

            </div>
        );
    }
}
