import React from 'react';
import Card from 'react-bootstrap/Card'
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button'
import axios from 'axios';
export class ShowCurrentItems extends React.Component {
    constructor(){
        super();
        this.DeleteItems=this.DeleteItems.bind(this);
    }
    DeleteItems(e){
        e.preventDefault();
        axios.delete('http://localhost:4000/api/items/'+this.props.item._id) //Sends to server Item id to delete
        .then(()=>{
            this.props.ReloadData();//Reloads data
        })
        .catch((error)=>{console.log(error+" error in delete")});
    }

    render() {
        return (
            <div class="container">
               
                <Card> 
                    <Card.Body>
                        <blockquote className="blockquote mb-0">
                            <img src={this.props.item.Image} width="200" height="200"></img> 
                            <Card.Header> <h4>{this.props.item.Name}</h4></Card.Header>
                            <p>{this.props.item.Stock}</p>
                            <p>$ {this.props.item.Price}</p>
                        </blockquote>
                    </Card.Body>
                </Card>
                

            </div>

        );
    }
}
