import React from 'react';
import Card from 'react-bootstrap/Card'
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button'
import axios from 'axios';
export class Itemsinfo extends React.Component {
    constructor(){
        super();
        this.DeleteItems=this.DeleteItems.bind(this);
    }
    DeleteItems(e){
        e.preventDefault();
        axios.delete('http://localhost:4000/api/items/'+this.props.item._id) //Sends to server Item id to delete
        .then(()=>{
            this.props.ReloadData();//Reloads data
        })
        .catch((error)=>{console.log(error+" error in delete")});
    }

    render() {
        return (
            <div class="container">
               
                <Card>
                    <Card.Header> <h4>{this.props.item.Name}</h4></Card.Header> 
                    <Card.Body>
                        <blockquote className="blockquote mb-0">
                            <img src={this.props.item.Price} width="200" height="200"></img> 
                            <p>{this.props.item.Stock}</p>
                        </blockquote>
                    </Card.Body>
                    <Link to={"/edit/"+this.props.item._id} className="btn btn-primary">Edit</Link>
                    <Button variant="danger" onClick={this.DeleteItems}>Delete</Button>
                </Card>
                
            </div>
            //Creates a card for movie
            //Sets info as title,img,year
            //Has button to delete movie by id
        );
    }
}
